import style from "./Card.module.css";

const littleC = document.querySelectorAll('.card');

littleC.forEach((card) => {
  card.addEventListener('click', () => {
    card.classList.toggle('flipped');
  });
});

const Card = ({id, name, status, species, gender, origin, image, onClose}) => {
   return (
      <div className= {style.cardItem}>
         <button className= {style.botonClose}onClick={onClose}>X</button>
         <h2 id= {style.id}>{"#" + id}</h2>
         <hr />
         <h2 id= {style.origin}>Origin: {origin}</h2>
         <h2 id= {style.nameChar}>{name}</h2>
         <div className={style.container}>
            <img id= {style.imagen} src={image} alt='' />
         </div>
         <div className= {style.littleCards}>
         <h2 id= {style.descriptionOne}>Status: {status}</h2>
         <h2 id= {style.descriptionThree}>Gender: {gender}</h2>
         <h2 id= {style.descriptionTwo}>Specie: {species}</h2>
         </div>
      </div>
   );
}

export default Card;
