import './App.css';
import React, { useState } from "react";
import Cards from "./components/Cards";
import Nav from "./components/Nav";
import  axios  from 'axios' ;

function App() {

const [ characters, setCharacters] = useState ([
      {
      id: 1,
      name: 'Rick Sanchez',
      status: 'Alive',
      species: 'Human',
      gender: 'Male',
      origin: {
         name: 'Earth (C-137)',
         url: 'https://rickandmortyapi.com/api/location/1',
      },
      image: 'https://rickandmortyapi.com/api/character/avatar/1.jpeg',
      }
]);

   const onSearch = (id) => {
      axios(`https://rickandmortyapi.com/api/character/${id}`)
      .then(({ data }) => {
         if (data.name) {
           const characterExists = characters.some(
             (character) => character.id === data.id
           );
   
           if (!characterExists) {
             setCharacters([...characters, data]);
           } else {
             window.alert('¡Este personaje ya se encuentra en la lista!');
           }
         } else {
           window.alert('¡No hay personajes con este ID!');
         }
       })
       .catch((error) => {
         console.log("Error al buscar personaje:", error);
       });
   };

   const onClose = (id) => {
      const filteredCharacters = characters.filter((character) => character.id !== parseInt(id)
      );
      setCharacters(filteredCharacters);
   };


   return (
      <div className='App'>
         <div>
         <Nav onSearch={onSearch} />
         </div>
         <div>
            <Cards
            characters={characters} 
            onClose= {onClose}
            />
         </div>
      </div>
   );
}

export default App;
