import style from "./SearchBar.module.css";
import {useState} from "react";

const SearchBar = ({onSearch}) => {
   const [ character, setCharacter] = useState ("") // cuando se trata del input va string vacio

   const handleChange = (event) => { // todo lo que escribamos lo vamos seteando
      setCharacter(event.target.value)  //esto es siempre asi con el input
   };

   return (
      <div className= {style.navBar}>
         <input id= {style.input} type='Search' value= {character} onChange= {handleChange}/>
         <button id={style.buttonSearch} onClick={() =>props.onSearch(character)}>Agregar</button> {/**la arrow fc evita que, si paso un param. la funcion se ejecute inmediatamente sin hacer click */}
         <h1>Integrating project: Rick and Morty</h1>
      </div>
   );
}

export default SearchBar;
