import './App.css';
import React, { useState } from "react";
import Cards from "./components/Cards";
import Nav from "./components/Nav/Nav";
import  axios  from 'axios' ;
import About from './components/About';
import Detail from './components/Detail';
import {Routes, Route, useLocation, Form} from "react-router-dom";

function App() {

const [ characters, setCharacters] = useState ([
      {
      id: 1,
      name: 'Rick Sanchez',
      status: 'Alive',
      species: 'Human',
      gender: 'Male',
      origin: {
         name: 'Earth (C-137)',
         url: 'https://rickandmortyapi.com/api/location/1',
      },
      image: 'https://rickandmortyapi.com/api/character/avatar/1.jpeg',
      }
]);

   const onSearch = (id) => {
      axios(`https://rickandmortyapi.com/api/character/${id}`)
      .then(({ data }) => {
         if (data.name) {
            const characterExists = characters.some(
            (character) => character.id === data.id
            );
   
         if (!characterExists) {
            setCharacters([...characters, data]);
            } else {
            window.alert('¡Este personaje ya se encuentra en la lista!');
            }
         }
      })
   };

   const onClose = (id) => {
      const filteredCharacters = characters.filter((character) => character.id !== parseInt(id)
      );
      setCharacters(filteredCharacters);
   };

   const addRandomCharacter = () => {
      axios("https://rickandmortyapi.com/api/character/")
      .then(({ data }) => {
         const randomCharacter = data.results[Math.floor(Math.random() * data.results.length)];
         const characterExists = characters.some(
            (character) => character.id === randomCharacter.id);
         if (!characterExists) {
            setCharacters([...characters, randomCharacter]);
         } else {
            window.alert('¡Este personaje ya se encuentra en la lista!');
         }
      })
   }

   const location = useLocation();


   return (
      <div className='App'>
         <Nav onSearch={onSearch} addRandomCharacter={addRandomCharacter} />
         {location.pathname !== '/' && <Nav />}
         <Routes>
            <Route path= "/" element={<Cards characters={characters} element={<Form />} onClose= {onClose} />} />
            <Route path= "/About" element={<About />} />
            <Route path= "/Detail/:id" element={<Detail />} />
            <Route path= "*" element={<h1>404</h1>} />
         </Routes>
         
      </div>
   );
}

export default App;
