import {ADD_FAV, REMOVE_FAV} from "./actions.types";

const initialState = {
    myFavorites: [],
}

const reducer = (state= initialState, action) => {
    switch (action.type) {
        case ADD_FAV:
            return {
                ...state,
                myFavorites: [...state.myFavorites, action.payload]
            }

        case REMOVE_FAV:
            let deleteCharacter = state.mayFavorites.filter (character => character.id !== Number (action.payload)) //guardamos el eliminado
            return {
                ...state,
                myFavorites: deleteCharacter
            }

        default:
            return {
                ...state
            }
    }

}

export default reducer;
