]);

   const onSearch = ({character}) => {
      setCharacters ([
         ...characters,
      ])
   };

   return (
      <div className='App'>
         <Nav onSearch={onSearch} />
         <div>
            <Cards
            characters={characters} 
            />
         </div>
      </div>
   );
}

export default App;
