import style from "./Card.module.css";
import { Link } from "react-router-dom";
import { connect } from "react-redux";
import { addFav, removeFav } from "../../redux/actions";
import { useState } from "react";

const Card = (props) => {
   const {id, name, status, species, gender, origin, image, onClose, addFav, removeFav} = props
   
   const [ isFav, setIsFav ] = useState(false)

   const handleFavorite = () => {
     isFav ? removeFav(id) : addFav(props)  // si el estado es falso, despachame a removeFav(id), sino addFav(props), osea si es falso se remueve si es true se agrega
    setIsFav(!isFav)  // aca seteamos si esta false lo pasamos a true y viceversa

   }

   return (
      <div className= {style.cardContainer}>
         {
         isFav ? (<button onClick={handleFavorite}>‚ù§Ô∏è</button>) : (<button onClick={handleFavorite}>ü§ç</button>)
         }
         
         <button className= {style.botonClose} onClick={() => onClose(id)}>X</button>
         <h2 id= {style.id}>{id}</h2>
         <Link to= {`/detail/${id}`}>
            <h2 id= {style.nameChar}>{name}</h2>
         </Link>
         <div className={style.container}>
            <img id= {style.imagen} src={image} alt='img not found' />
         </div>
         <div className= {style.littleCards}>
         <h2 id= {style.descriptionOne}>{origin}</h2>
         <h2 id= {style.descriptionTwo}>Status: {status}</h2>
         <h2 id= {style.descriptionThree}>Gender: {gender}</h2>
         <h2 id= {style.descriptionFour}>Specie: {species}</h2>
         </div>

      </div>
   );
}

const mapDispatchToProps = (dispatch) => {
   return {
      addFav: (character) => dispatch(addFav(character)), 
      removeFav: (id) => dispatch (removeFav(id))
   }
}

export default connect (null, mapDispatchToProps)(Card);
